document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".your-bag__grid__item__btn").forEach((e=>e.addEventListener("click",(e=>function(e){const t=e.target.closest(".your-bag__grid__item"),i=t.querySelector(".your-bag__grid__item__info");if(i){document.querySelectorAll(".your-bag__grid__item__info").forEach((e=>{e.classList.remove("active")})),i.classList.add("active")}}(e))))),window.addEventListener("click",(e=>function(e){const t=e.target.closest(".your-bag__grid__item");if(!t){document.querySelectorAll(".your-bag__grid__item__info").forEach((e=>{e.classList.remove("active")}))}}(e)),{passive:!0})}));