document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector("#open-notification"),t=document.querySelector("#notification");e.addEventListener("click",(e=>{e.preventDefault(),t.classList.add("active")})),window.addEventListener("click",(e=>{const t=e.target;if(!t.closest("#notification")&!t.closest("#open-notification")){document.querySelector("#notification").classList.remove("active")}}),{passive:!0})})),window.addEventListener("load",(()=>{const e=document.querySelector("#notification");window.addEventListener("scroll",(()=>{e.classList.contains("active")&&e.classList.remove("active")}))}));